import React, {Component} from 'react';
import './NewItem.css'

class NewItem extends Component{

    constructor(props){
        super(props);
        this.state={
            inputContent: ''
        }
    }

    onInputChange = (event) => {
        this.setState({
            inputContent: event.target.value
        })
    }
    
    onAddBtnClick = () =>{
        if (this.state.inputContent === ''){
            this.setState({
                inputContent: 'Input is null.'
            })
        }else{
        this.props.addItem(this.state.inputContent)
        this.setState({
            inputContent: ''
        })}
    }
    //return一个标签
    render(){
        return(
            <div> 
            
            <input className="input" value={this.state.inputContent} onChange={this.onInputChange}/>
            <button onClick={this.onAddBtnClick}>Add To-do</button>

        </div>
        ) 
        
    }
    
}
export default NewItem;
