import React, {Component} from 'react';
import './ListItem.css'


class ListItem extends Component{
    constructor(props){
        super(props)
        this.setState({
            doneChange: false,
            changeItem: ''
        })
    } 
    
    onDoneChange = (event) => {
        this.setState({
            doneChange: true,
            changeItem: event.target.value
        })
    }

    onChangeDone = () =>{
        this.props.cDone(this.state.changeItem)
        this.setState({
            inputContent: ''
        })
    }
    
    render(){

        const item = this.props.item;
        if (item.done) {
            return <p className="done-item">{item.content}</p>
        } else {
            return <p onClick={this.onDoneChange} onChange={this.onChangeDone} className="item">{item.content}</p>
        }
    }
}

